<?php

namespace App\Http\Controllers;

use Illuminate\Http\Request;
use Illuminate\Support\Facades\Auth;
use App\Models\Transaction;

class DashboardController extends Controller
{
    /**
     * Show the dashboard page.
     */
    public function index()
    {
        // Ambil pengguna yang sedang login
        $user = Auth::user();

        // Ambil 5 transaksi terbaru dari pengguna
        $transactions = $user->transactions()->latest()->take(5)->get();

        // Data iklan/promo (contoh statis)
        $promotions = [
            [
                'title' => 'Top-Up Bonus!',
                'description' => 'Dapatkan bonus Rp50,000 untuk top-up pertama Anda.',
                'image' => 'data:image/jpeg;base64,',
            ],
            [
                'title' => 'Transfer Gratis!',
                'description' => 'Nikmati transfer gratis ke semua bank hingga akhir bulan.',
                'image' => 'data:image/jpeg;base64,',
            ],
            [
                'title' => 'Cashback 50%!',
                'description' => 'Dapatkan cashback hingga 50% untuk pembayaran tagihan.',
                'image' => 'data:image/jpeg;base64,',
            ],
        ];

        // Kirim data ke view
        return view('dashboard', compact('user', 'transactions', 'promotions'));
    }
}
